import React from "react";

const Modal = ({ showModal, image, onClose }) => {
  if (!showModal) return null;

  return (
    <div className="fixed inset-0 z-50 flex justify-center items-center">
      {/* Dark Overlay */}
      <div
        className="fixed inset-0 bg-black opacity-50"
        onClick={onClose}
      ></div>

      {/* Modal Content */}
      <div className="relative bg-white rounded-lg overflow-hidden shadow-lg max-w-3xl z-50">
        <button
          className="text-white bg-red-500 rounded-full p-5 absolute top-0 right-0 m-4"
          onClick={onClose}
        >
          &times;
        </button>
        <img
          src={image.webformatURL}
          alt={image.webformatURL}
          className="w-full"
        />
        <div className="p-4">
          <h2 className="text-2xl font-bold">{image.user}</h2>
          <p>{image.tags}</p>
        </div>
      </div>
    </div>
  );
};

export default Modal;
