import React, { useState, useEffect } from "react";
import ImageCard from "./components/imageCard";
import VideoCard from "./components/videoCard";
import ImageSearch from "./components/imageSearch";
import Modal from "./components/modal";

function App() {
  const [media, setMedia] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [term, setTerm] = useState("");
  const [searchType, setSearchType] = useState("images");
  const [showModal, setShowModal] = useState(false);
  const [selectedMedia, setSelectedMedia] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [mediaPerPage] = useState(6);
  const [totalMedia, setTotalMedia] = useState(0);

  useEffect(() => {
    setIsLoading(true);
    const url =
      searchType === "images"
        ? `https://pixabay.com/api/?key=${process.env.REACT_APP_PIXABAY_API_KEY}&q=${term}&image_type=photo&pretty=true&page=${currentPage}&per_page=${mediaPerPage}`
        : `https://pixabay.com/api/videos/?key=${process.env.REACT_APP_PIXABAY_API_KEY}&q=${term}&pretty=true&page=${currentPage}&per_page=${mediaPerPage}`;

    console.log(url);
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        setMedia(data.hits);
        setTotalMedia(data.totalHits);
        setIsLoading(false);
      })
      .catch((err) => console.log(err));
  }, [term, searchType, currentPage, mediaPerPage]);

  const handleMediaClick = (mediaItem) => {
    setSelectedMedia(mediaItem);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedMedia(null);
  };

  const nextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const prevPage = () => {
    setCurrentPage((prevPage) => (prevPage > 1 ? prevPage - 1 : 1));
  };

  const totalPages = Math.ceil(totalMedia / mediaPerPage);

  return (
    <div className="mx-auto px-4 pb-4 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold text-center mx-auto pt-10">
        Stunning Free Media
      </h1>
      <p className="text-sm text-center mx-auto pt-2">
        Stock images and videos from Pixabay API!
      </p>
      <ImageSearch
        searchText={(text, type) => {
          setTerm(text);
          setSearchType(type);
          setCurrentPage(1); // Reset to the first page on new search
        }}
      />

      {!isLoading && media.length === 0 && (
        <h1 className="text-4xl text-center mx-auto">No Results Found</h1>
      )}
      {isLoading ? (
        <h1 className="text-6xl text-center mx-auto mt-32">Loading...</h1>
      ) : (
        <>
          <div className="flex justify-center">
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
              {media.map((item) =>
                searchType === "images" ? (
                  <ImageCard
                    key={item.id}
                    image={item}
                    onClick={handleMediaClick}
                  />
                ) : (
                  <VideoCard
                    key={item.id}
                    video={item}
                    onClick={handleMediaClick}
                  />
                )
              )}
            </div>
          </div>
          <div className="flex justify-center my-4">
            {currentPage > 1 && (
              <button
                onClick={prevPage}
                className="bg-purple-500 text-white px-4 py-2 mx-2 rounded hover:bg-purple-600"
              >
                Previous
              </button>
            )}
            <span className="text-gray-700 px-4 py-2 mx-2 rounded">
              Page {currentPage} of {totalPages}
            </span>
            {currentPage < totalPages && (
              <button
                onClick={nextPage}
                className="bg-purple-500 text-white px-4 py-2 mx-2 rounded hover:bg-purple-600"
              >
                Next
              </button>
            )}
          </div>
        </>
      )}
      <Modal
        showModal={showModal}
        media={selectedMedia}
        onClose={handleCloseModal}
      />
      <p className="text-sm text-center text-gray-400 mx-auto pt-2">
        created by Marielle Harrell
      </p>
    </div>
  );
}

export default App;
