import React from "react";

const ImageCard = ({ image, onClick }) => {
  const tags = image.tags.split(",");

  return (
    <div
      className="max-w-sm rounded overflow-hidden shadow-lg cursor-pointer bg-white hover:bg-purple-50"
      onClick={() => onClick(image)}
    >
      <img
        className="w-full"
        src={image.webformatURL}
        alt={image.webformatURL}
      />
      <div className="px-6 py-4">
        <div className="font-bold text-purple-500 text-xl mb-2">
          Photo by {image.user}
        </div>
        <ul>
          <li>
            <strong> Views:</strong> {image.views}
          </li>
          <li>
            <strong>Downloads:</strong> {image.downloads}
          </li>
          <li>
            <strong>Likes:</strong> {image.likes}
          </li>
        </ul>
      </div>
      <div className="px-6 py-4"></div>
      {tags.map((tag, index) => (
        <span
          key={index}
          className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-4 ml-2"
        >
          #{tag}
        </span>
      ))}
    </div>
  );
};

export default ImageCard;
